INSERT INTO `blogs` (`id`, `category_id`, `name`, `email`, `image`, `author`, `short_description`, `long_description`, `status`, `created_at`, `updated_at`) VALUES
(1, '1', 'আবার অতিমানব ম্যাক্সওয়েল, অস্ট্রেলিয়ার অবিশ্বাস্য জয়', 'admin@admin.com', 'uploads/Blog_images/prothomalo-bangla_2023-11_24dac0f2-4488-470d-ac1b-849dc548d39a_CRICKET_IND_AUS_T20_170650.webp', 'Tanvir Hasan Nahid', 'এখন গ্লেন ম্যাক্সওয়েল ক্রিজে থাকা মানেই অসম্ভব যেকোনো কিছুকেই সম্ভব বানিয়ে ফেলার আশা।', '<div id=\"1e56ac63-2c7f-4598-8841-b547b60d8c22\" style=\"color: rgb(0, 0, 0); font-family: Shurjo, SolaimanLipi, &quot;Siyam Rupali&quot;, Roboto, Arial, Helvetica, monospace; font-size: 18px;\"><div class=\"storyCard eyOoS\" style=\"--borderColor: var(--primaryColor); color: var(--black); font-size: var(--fs-13); margin: var(--space2_4) auto 0; max-width: 622px;\"><div class=\"   \r\n                  story-element\" style=\"margin-bottom: var(--space1_6);\"><div class=\"story-element story-element-text\" style=\"margin: 0px auto; max-width: 622px; padding: 0px;\"><p style=\"font-family: var(--font-2); margin-right: 0px; margin-bottom: var(--space1_6); margin-left: 0px; padding: 0px; font-size: var(--fs-18); line-height: 1.7; white-space-collapse: break-spaces;\">খন গ্লেন ম্যাক্সওয়েল ক্রিজে থাকা মানেই অসম্ভব যেকোনো কিছুকেই সম্ভব বানিয়ে ফেলার আশা। বিশ্বকাপে আফগানিস্তানের বিপক্ষে সেই অবিশ্বাস্য ইনিংসের পর থেকে নিজেকে এমনই উচ্চতায় নিয়ে গেছেন অস্ট্রেলিয়ান অলরাউন্ডার। </p></div></div><div class=\"reference-link\" style=\"align-items: baseline; color: var(--black); font-size: var(--fs-20); font-weight: var(--bold); margin: var(--space2_4) 0;\"><a aria-label=\"প্রথম ২ ম্যাচ হারা অস্ট্রেলিয়া যে মন্ত্র পড়ে জিতেছে বিশ্বকাপ\" target=\"_self\" rel=\"next search\" href=\"https://www.prothomalo.com/sports/cricket/n0jc4tadxo\" style=\"align-items: baseline; display: flex; margin: 0 var(--space2_0) var(--space1_6) 0;\"><span class=\"reference-icon\" style=\"flex-shrink: 0;\"><svg xmlns=\"http://www.w3.org/2000/svg\" width=\"14\" height=\"14\" viewBox=\"0 0 14 14\"><path id=\"Path_155\" d=\"M15.444 15.444H4.556V4.556H9V3H4.556A1.555 1.555 0 0 0 3 4.556v10.888A1.555 1.555 0 0 0 4.556 17h10.888A1.56 1.56 0 0 0 17 15.444V11h-1.556zM11.556 3v1.556h2.792L6.7 12.2l1.1 1.1 7.646-7.646v2.79H17V3z\" fill=\"#121212\" data-name=\"Path 155\" transform=\"translate(-3 -3)\"></path></svg></span><span class=\"reference-link-text\" style=\"line-height: 1.5; margin-left: var(--space0_8); text-decoration-line: underline; text-underline-offset: 6px;\">প্রথম ২ ম্যাচ হারা অস্ট্রেলিয়া যে মন্ত্র পড়ে জিতেছে বিশ্বকাপ</span></a></div><div class=\"   \r\n                  story-element\" style=\"margin-bottom: var(--space1_6);\"><div class=\"story-element story-element-text\" style=\"margin: 0px auto; max-width: 622px; padding: 0px;\"><p style=\"font-family: var(--font-2); margin-right: 0px; margin-bottom: var(--space1_6); margin-left: 0px; padding: 0px; font-size: var(--fs-18); line-height: 1.7; white-space-collapse: break-spaces;\">মুম্বাইয়ে বিশ্বকাপের সেই ম্যাচে ১২৮ বলে অপরাজিত ২০১ রানের সেই অবিশ্বাস্য ইনিংসের পর গতকাল আবার দেখা গেল অতিমানব ম্যাক্সওয়েলকে। এবার প্রতিপক্ষ ভারত, ম্যাচটা টি-টোয়েন্টি। ভারতের তোলা ৩ উইকেটে ২২২ তাড়া করতে নেমে অস্ট্রেলিয়াও ভীষণ চাপে। কিন্তু ম্যাক্সওয়েল থাকলে কিছুই যে অসম্ভব না! ৪৮ বলে অপরাজিত ১০৪ রান করার পথে ম্যাক্সওয়েল গড়লেন টি-টোয়েন্টিতে অস্ট্রেলিয়ার হয়ে দ্রুততম শতকের রেকর্ড। ম্যাচটা অস্ট্রেলিয়া জিতল একেবারে শেষ বলে ম্যাক্সওয়েলের মারা চারেই। ৫ উইকেটে দুর্দান্ত জয়ে সিরিজেও ঘুরে দাঁড়াল অস্ট্রেলিয়া। পাঁচ টি-টোয়েন্টির সিরিজে তিনটি ম্যাচ শেষে আপাতত ভারত এগিয়ে ২-১ ব্যবধানে।</p></div></div><div class=\"   \r\n                  story-element\" style=\"margin-bottom: var(--space1_6);\"><div class=\"story-element story-element-text story-element-text-also-read\" style=\"margin: 0px auto; max-width: 622px; padding: 0px;\"><span class=\"also-read lDfA5\"><div class=\"also-read-text\" style=\"color: var(--scorpion); font-size: var(--fs-16); font-weight: var(--bold); margin-bottom: var(--space0_8);\">আরও পড়ুন</div><a target=\"_self\" aria-label=\"sports/cricket/5beso28ybd\" class=\"card-with-image-zoom _-2jpv\" href=\"https://www.prothomalo.com